import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import ExamService from '../services/ExamService';

const ExamDetails = () => {
    const { examId } = useParams();
    const navigate = useNavigate();
    const { user } = useAuth();
    const [exam, setExam] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const handleDeleteExam = async () => {
        if (window.confirm('Are you sure you want to delete this exam? This action cannot be undone.')) {
            try {
                setLoading(true);
                await ExamService.deleteExam(examId);
                navigate('/teacher-dashboard', { state: { message: 'Exam deleted successfully' } });
            } catch (err) {
                console.error('Error deleting exam:', err);
                const errorMessage = err.message || 'Failed to delete exam. Please try again.';
                setError(errorMessage);
                setLoading(false);
            }
        }
    };

    useEffect(() => {
        // Check if user is logged in
        if (!user) {
            navigate('/login');
            return;
        }

        // Fetch exam details
        const fetchExam = async () => {
            try {
                setLoading(true);
                const examData = await ExamService.getExamById(examId);
                setExam(examData);
                setLoading(false);
            } catch (err) {
                console.error('Error fetching exam:', err);
                setError('Failed to load exam details. Please try again.');
                setLoading(false);
            }
        };

        fetchExam();
    }, [examId, navigate, user]);

    if (loading) {
        return (
            <div className="min-h-screen bg-gray-50 flex items-center justify-center">
                <div className="spinner-border text-primary" role="status">
                    <span className="sr-only">Loading...</span>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="min-h-screen bg-gray-50 flex items-center justify-center">
                <div className="bg-white p-8 rounded-lg shadow-md max-w-md w-full">
                    <h2 className="text-2xl font-bold text-red-600 mb-4">Error</h2>
                    <p className="text-gray-700">{error}</p>
                    <button
                        onClick={() => navigate(-1)}
                        className="mt-6 bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    >
                        Go Back
                    </button>
                </div>
            </div>
        );
    }

    if (!exam) {
        return (
            <div className="min-h-screen bg-gray-50 flex items-center justify-center">
                <div className="bg-white p-8 rounded-lg shadow-md max-w-md w-full">
                    <h2 className="text-2xl font-bold text-gray-800 mb-4">Exam Not Found</h2>
                    <p className="text-gray-700">The exam you're looking for doesn't exist or you don't have permission to access it.</p>
                    <button
                        onClick={() => navigate(-1)}
                        className="mt-6 bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    >
                        Go Back
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="max-w-4xl mx-auto p-6">
            <div className="bg-white rounded-lg shadow-card overflow-hidden">
                {/* Exam Header */}
                <div className="bg-accent-100 text-white p-5">
                    <h1 className="text-2xl font-bold">{exam.title}</h1>
                    <p className="text-light-200 mt-1">{exam.description}</p>
                </div>

                {/* Exam Details */}
                <div className="p-6">
                    <h2 className="text-xl font-semibold mb-4">Exam Details</h2>
                    <div className="grid grid-cols-2 gap-4 mb-6">
                        <div className="flex items-center">
                            <span className="text-dark-500 w-24">Type:</span>
                            <span className="font-medium">{exam.type}</span>
                        </div>
                        <div className="flex items-center">
                            <span className="text-dark-500 w-24">Duration:</span>
                            <span className="font-medium">{exam.duration} minutes</span>
                        </div>
                        <div className="flex items-center">
                            <span className="text-dark-500 w-24">Start Time:</span>
                            <span className="font-medium">{exam.startTime}</span>
                        </div>
                        <div className="flex items-center">
                            <span className="text-dark-500 w-24">End Time:</span>
                            <span className="font-medium">{exam.endTime}</span>
                        </div>
                    </div>

                    {/* Questions Section */}
                    <h2 className="text-xl font-semibold mb-4">Questions</h2>
                    <div className="space-y-4">
                        {exam.questions.map((question, index) => (
                            <div key={index} className="bg-light-200 p-4 rounded-md hover:shadow-md transition-shadow">
                                <h3 className="font-medium">Question {index + 1}: {question.text}</h3>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Action Buttons */}
                <div className="bg-light-200 p-4 flex justify-between">
                    <button 
                        className="bg-dark-300 hover:bg-dark-400 text-white py-2 px-6 rounded-md transition-colors"
                        onClick={() => navigate(-1)}
                    >
                        Back
                    </button>
                    <div className="space-x-3">
                        <button 
                            className="bg-accent-100 hover:bg-accent-200 text-white py-2 px-6 rounded-md transition-colors"
                            onClick={() => navigate(`/edit-exam/${exam.id}`)}
                        >
                            Edit Exam
                        </button>
                        <button 
                            className="bg-green-600 hover:bg-green-700 text-white py-2 px-6 rounded-md transition-colors"
                            onClick={() => navigate(`/add-questions/${exam.id}`)}
                        >
                            Add Questions
                        </button>
                        <button 
                            className="bg-red-600 hover:bg-red-700 text-white py-2 px-6 rounded-md transition-colors"
                            onClick={handleDeleteExam}
                        >
                            Delete Exam
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ExamDetails;
