import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import config from '../config';

const StudentDashboard = () => {
    const [activeTab, setActiveTab] = useState('courses');
    const [courses, setCourses] = useState([]);
    const [exams, setExams] = useState([]);
    const [results, setResults] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const { user, logout } = useAuth();

    useEffect(() => {
        if (activeTab === 'courses') {
            fetchCourses();
        } else if (activeTab === 'exams') {
            fetchExams();
        } else if (activeTab === 'results') {
            fetchResults();
        }
    }, [activeTab]);

    const fetchCourses = async () => {
        try {
            setLoading(true);
            setError('');
            const token = localStorage.getItem('token');
            const response = await axios.get(`${config.API_BASE_URL}/api/students/courses`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setCourses(response.data.courses || []);
        } catch (err) {
            setError('Failed to fetch courses');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const fetchExams = async () => {
        try {
            setLoading(true);
            setError('');
            const token = localStorage.getItem('token');
            const response = await axios.get(`${config.API_BASE_URL}/api/students/exams`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setExams(response.data.exams || []);
        } catch (err) {
            setError('Failed to fetch exams');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const fetchResults = async () => {
        try {
            setLoading(true);
            setError('');
            const token = localStorage.getItem('token');
            const response = await axios.get(`${config.API_BASE_URL}/api/students/results`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setResults(response.data.results || []);
        } catch (err) {
            setError('Failed to fetch results');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = () => {
        logout();
    };

    const startExam = async (examId) => {
        try {
            // Logic to start an exam
            console.log(`Starting exam ${examId}`);
        } catch (err) {
            setError('Failed to start exam');
            console.error(err);
        }
    };

    return (
        <div className="min-h-screen bg-gray-100">
            <div className="flex h-screen">
                {/* Sidebar - Desktop */}
                <div className="hidden md:flex md:flex-col md:w-64 md:fixed md:inset-y-0 bg-gradient-to-b from-blue-700 to-indigo-900">
                    <div className="flex items-center justify-center h-16 px-4 bg-blue-800">
                        <h1 className="text-xl font-bold text-white">Exam Platform</h1>
                    </div>
                    <div className="flex flex-col flex-1 overflow-y-auto">
                        <nav className="flex-1 px-4 py-4 space-y-2">
                            <button
                                onClick={() => setActiveTab('courses')}
                                className={`flex items-center w-full px-4 py-3 text-sm font-medium rounded-lg ${
                                    activeTab === 'courses'
                                        ? 'bg-white text-blue-700'
                                        : 'text-white hover:bg-blue-800'
                                }`}
                            >
                                <svg 
                                    className={`mr-3 h-5 w-5 ${activeTab === 'courses' ? 'text-blue-700' : 'text-blue-300'}`}
                                    xmlns="http://www.w3.org/2000/svg" 
                                    fill="none" 
                                    viewBox="0 0 24 24" 
                                    stroke="currentColor"
                                >
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.747 0 3.332.477 4.5 1.253v13C19.832 18.477 18.247 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
                                </svg>
                                My Courses
                            </button>
                            
                            <button
                                onClick={() => setActiveTab('exams')}
                                className={`flex items-center w-full px-4 py-3 text-sm font-medium rounded-lg ${
                                    activeTab === 'exams'
                                        ? 'bg-white text-blue-700'
                                        : 'text-white hover:bg-blue-800'
                                }`}
                            >
                                <svg 
                                    className={`mr-3 h-5 w-5 ${activeTab === 'exams' ? 'text-blue-700' : 'text-blue-300'}`}
                                    xmlns="http://www.w3.org/2000/svg" 
                                    fill="none" 
                                    viewBox="0 0 24 24" 
                                    stroke="currentColor"
                                >
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                                </svg>
                                Available Exams
                            </button>
                            
                            <button
                                onClick={() => setActiveTab('results')}
                                className={`flex items-center w-full px-4 py-3 text-sm font-medium rounded-lg ${
                                    activeTab === 'results'
                                        ? 'bg-white text-blue-700'
                                        : 'text-white hover:bg-blue-800'
                                }`}
                            >
                                <svg 
                                    className={`mr-3 h-5 w-5 ${activeTab === 'results' ? 'text-blue-700' : 'text-blue-300'}`}
                                    xmlns="http://www.w3.org/2000/svg" 
                                    fill="none" 
                                    viewBox="0 0 24 24" 
                                    stroke="currentColor"
                                >
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                                </svg>
                                My Results
                            </button>
                        </nav>
                        
                        <div className="p-4 mt-auto border-t border-blue-800">
                            <div className="flex items-center">
                                <div className="h-10 w-10 rounded-full bg-blue-600 flex items-center justify-center text-white font-semibold">
                                    {user?.firstName?.charAt(0)}{user?.lastName?.charAt(0)}
                                </div>
                                <div className="ml-3">
                                    <p className="text-sm font-medium text-white">
                                        {user?.firstName} {user?.lastName}
                                    </p>
                                    <button
                                        onClick={handleLogout}
                                        className="text-xs font-medium text-blue-200 hover:text-white"
                                    >
                                        Logout
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                
                {/* Main Content */}
                <div className="flex-1 md:ml-64">
                    {/* Mobile Header */}
                    <div className="md:hidden bg-white shadow-sm">
                        <div className="flex items-center justify-between px-4 py-3">
                            <h1 className="text-lg font-semibold text-gray-900">Exam Platform</h1>
                            <div className="flex items-center space-x-3">
                                <div className="h-8 w-8 rounded-full bg-blue-600 flex items-center justify-center text-white font-semibold">
                                    {user?.firstName?.charAt(0)}{user?.lastName?.charAt(0)}
                                </div>
                                <button
                                    onClick={handleLogout}
                                    className="text-sm text-red-600 hover:text-red-800"
                                >
                                    Logout
                                </button>
                            </div>
                        </div>
                        
                        {/* Mobile Tabs */}
                        <div className="flex border-b border-gray-200">
                            <button
                                onClick={() => setActiveTab('courses')}
                                className={`flex-1 py-3 text-sm font-medium text-center ${
                                    activeTab === 'courses'
                                        ? 'border-b-2 border-blue-500 text-blue-600'
                                        : 'text-gray-500'
                                }`}
                            >
                                Courses
                            </button>
                            <button
                                onClick={() => setActiveTab('exams')}
                                className={`flex-1 py-3 text-sm font-medium text-center ${
                                    activeTab === 'exams'
                                        ? 'border-b-2 border-blue-500 text-blue-600'
                                        : 'text-gray-500'
                                }`}
                            >
                                Exams
                            </button>
                            <button
                                onClick={() => setActiveTab('results')}
                                className={`flex-1 py-3 text-sm font-medium text-center ${
                                    activeTab === 'results'
                                        ? 'border-b-2 border-blue-500 text-blue-600'
                                        : 'text-gray-500'
                                }`}
                            >
                                Results
                            </button>
                        </div>
                    </div>
                    
                    {/* Content Area */}
                    <div className="p-6">
                        <div className="max-w-7xl mx-auto">
                            <div className="flex items-center justify-between mb-6">
                                <h1 className="text-2xl font-bold text-gray-900">
                                    {activeTab === 'courses' && 'My Courses'}
                                    {activeTab === 'exams' && 'Available Exams'}
                                    {activeTab === 'results' && 'My Results'}
                                </h1>
                                
                                {activeTab === 'courses' && (
                                    <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-blue-100 text-blue-800">
                                        {courses.length} Courses
                                    </span>
                                )}
                                
                                {activeTab === 'exams' && (
                                    <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-green-100 text-green-800">
                                        {exams.length} Exams
                                    </span>
                                )}
                                
                                {activeTab === 'results' && (
                                    <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-purple-100 text-purple-800">
                                        {results.length} Results
                                    </span>
                                )}
                            </div>
                            
                            {error && (
                                <div className="mb-6 bg-red-50 border-l-4 border-red-500 p-4 rounded-md">
                                    <div className="flex">
                                        <div className="flex-shrink-0">
                                            <svg className="h-5 w-5 text-red-500" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                                                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                                            </svg>
                                        </div>
                                        <div className="ml-3">
                                            <p className="text-sm text-red-700">{error}</p>
                                        </div>
                                    </div>
                                </div>
                            )}
                            
                            {loading ? (
                                <div className="flex justify-center py-12">
                                    <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
                                </div>
                            ) : (
                                <>
                                    {/* Courses Tab */}
                                    {activeTab === 'courses' && (
                                        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
                                            {courses.length > 0 ? (
                                                courses.map((course) => (
                                                    <div key={course.id} className="bg-white overflow-hidden shadow-lg rounded-lg transition-all duration-200 hover:shadow-xl">
                                                        <div className="h-3 bg-blue-600"></div>
                                                        <div className="px-6 py-5">
                                                            <h3 className="text-lg font-semibold text-gray-900 truncate">{course.name}</h3>
                                                            <div className="mt-2 text-sm text-gray-600">
                                                                <p>{course.description || 'No description available'}</p>
                                                            </div>
                                                            <div className="mt-4 flex items-center space-x-2">
                                                                <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                                                                    {course.department || 'Department'}
                                                                </span>
                                                                <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                                                                    Level {course.level || '1'}
                                                                </span>
                                                            </div>
                                                        </div>
                                                        <div className="bg-gray-50 px-6 py-3">
                                                            <a href="#" className="text-sm font-medium text-blue-600 hover:text-blue-800">
                                                                View course details →
                                                            </a>
                                                        </div>
                                                    </div>
                                                ))
                                            ) : (
                                                <div className="col-span-full flex flex-col items-center justify-center py-12 bg-white rounded-lg shadow">
                                                    <svg className="h-16 w-16 text-gray-400" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.747 0 3.332.477 4.5 1.253v13C19.832 18.477 18.247 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
                                                    </svg>
                                                    <h3 className="mt-4 text-lg font-medium text-gray-900">No courses found</h3>
                                                    <p className="mt-1 text-sm text-gray-500">You are not enrolled in any courses yet.</p>
                                                </div>
                                            )}
                                        </div>
                                    )}
                                    
                                    {/* Exams Tab */}
                                    {activeTab === 'exams' && (
                                        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
                                            {exams.length > 0 ? (
                                                exams.map((exam) => (
                                                    <div key={exam.id} className="bg-white overflow-hidden shadow-lg rounded-lg transition-all duration-200 hover:shadow-xl">
                                                        <div className="h-3 bg-green-600"></div>
                                                        <div className="px-6 py-5">
                                                            <h3 className="text-lg font-semibold text-gray-900 truncate">{exam.title}</h3>
                                                            <p className="mt-1 text-sm text-gray-600">{exam.description || 'No description available'}</p>
                                                            
                                                            <div className="mt-4 space-y-2">
                                                                <div className="flex items-center text-sm text-gray-500">
                                                                    <svg className="mr-1.5 h-5 w-5 text-gray-400" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                                                                    </svg>
                                                                    Duration: {exam.duration} minutes
                                                                </div>
                                                                
                                                                <div className="flex items-center text-sm text-gray-500">
                                                                    <svg className="mr-1.5 h-5 w-5 text-gray-400" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                                                                    </svg>
                                                                    {new Date(exam.startTime).toLocaleString()}
                                                                </div>
                                                                
                                                                <div className="flex items-center text-sm text-gray-500">
                                                                    <svg className="mr-1.5 h-5 w-5 text-gray-400" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                                                                    </svg>
                                                                    {exam.course?.name || 'Course name'}
                                                                </div>
                                                            </div>
                                                        </div>
                                                        <div className="bg-gray-50 px-6 py-3">
                                                            <button
                                                                onClick={() => startExam(exam.id)}
                                                                className="w-full inline-flex justify-center items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
                                                            >
                                                                Start Exam
                                                            </button>
                                                        </div>
                                                    </div>
                                                ))
                                            ) : (
                                                <div className="col-span-full flex flex-col items-center justify-center py-12 bg-white rounded-lg shadow">
                                                    <svg className="h-16 w-16 text-gray-400" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-3 7h3m-3 4h3m-6-4h.01M9 16h.01" />
                                                    </svg>
                                                    <h3 className="mt-4 text-lg font-medium text-gray-900">No exams available</h3>
                                                    <p className="mt-1 text-sm text-gray-500">There are no upcoming exams for your courses.</p>
                                                </div>
                                            )}
                                        </div>
                                    )}
                                    
                                    {/* Results Tab */}
                                    {activeTab === 'results' && (
                                        <div className="bg-white shadow-lg rounded-lg overflow-hidden">
                                            {results.length > 0 ? (
                                                <div className="overflow-x-auto">
                                                    <table className="min-w-full divide-y divide-gray-200">
                                                        <thead className="bg-gray-50">
                                                            <tr>
                                                                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                                    Exam
                                                                </th>
                                                                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                                    Course
                                                                </th>
                                                                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                                    Score
                                                                </th>
                                                                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                                    Date
                                                                </th>
                                                                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                                    Status
                                                                </th>
                                                                <th scope="col" className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                                    Actions
                                                                </th>
                                                            </tr>
                                                        </thead>
                                                        <tbody className="bg-white divide-y divide-gray-200">
                                                            {results.map((result) => (
                                                                <tr key={result.id}>
                                                                    <td className="px-6 py-4 whitespace-nowrap">
                                                                        <div className="text-sm font-medium text-gray-900">{result.exam?.title || 'Exam title'}</div>
                                                                    </td>
                                                                    <td className="px-6 py-4 whitespace-nowrap">
                                                                        <div className="text-sm text-gray-500">{result.course?.name || 'Course name'}</div>
                                                                    </td>
                                                                    <td className="px-6 py-4 whitespace-nowrap">
                                                                        <div className="text-sm text-gray-900">{result.score}/{result.totalScore}</div>
                                                                        <div className="text-sm text-gray-500">{Math.round((result.score / result.totalScore) * 100)}%</div>
                                                                    </td>
                                                                    <td className="px-6 py-4 whitespace-nowrap">
                                                                        <div className="text-sm text-gray-500">{new Date(result.submittedAt).toLocaleDateString()}</div>
                                                                    </td>
                                                                    <td className="px-6 py-4 whitespace-nowrap">
                                                                        <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                                                                            result.score / result.totalScore >= 0.7
                                                                                ? 'bg-green-100 text-green-800'
                                                                                : result.score / result.totalScore >= 0.5
                                                                                ? 'bg-yellow-100 text-yellow-800'
                                                                                : 'bg-red-100 text-red-800'
                                                                        }`}>
                                                                            {result.score / result.totalScore >= 0.7
                                                                                ? 'Passed'
                                                                                : result.score / result.totalScore >= 0.5
                                                                                ? 'Average'
                                                                                : 'Failed'}
                                                                        </span>
                                                                    </td>
                                                                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                                                                        <a href="#" className="text-blue-600 hover:text-blue-900">View Details</a>
                                                                    </td>
                                                                </tr>
                                                            ))}
                                                        </tbody>
                                                    </table>
                                                </div>
                                            ) : (
                                                <div className="flex flex-col items-center justify-center py-12">
                                                    <svg className="h-16 w-16 text-gray-400" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                                                    </svg>
                                                    <h3 className="mt-4 text-lg font-medium text-gray-900">No results found</h3>
                                                    <p className="mt-1 text-sm text-gray-500">You haven't completed any exams yet.</p>
                                                </div>
                                            )}
                                        </div>
                                    )}
                                </>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

                                                    <h3 className="mt-2 text-sm font-medium text-gray-900">
                                                        {course.courseCode || 'Course Code'}
                                                    </h3>
