import React, { createContext, useState, useContext, useEffect } from 'react';
import api from '../services/api';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Check if user is already logged in
        const checkLoggedIn = async () => {
            try {
                const token = localStorage.getItem('token');
                if (token) {
                    const response = await api.get('/api/auth/me');
                    setUser(response.data);
                }
            } catch (error) {
                console.error('Auth check error:', error);
                localStorage.removeItem('token');
            } finally {
                setLoading(false);
            }
        };

        checkLoggedIn();
    }, []);

    const login = async (email, password, role) => {
        try {
            const endpoint = role === 'teacher' ? '/api/teachers/login' : '/api/students/login';
            const response = await api.post(endpoint, { email, password });
            
            const { token, user: userData } = response.data;
            
            localStorage.setItem('token', token);
            
            // Check the structure of the response data and extract user info accordingly
            let userObject;
            if (userData) {
                // If userData already has role information, use it directly
                if (userData.role) {
                    userObject = userData;
                } 
                // If teacher data is nested in a teacher property
                else if (role === 'teacher' && userData.teacher) {
                    userObject = userData.teacher;
                    // Ensure role is set
                    userObject.role = 'teacher';
                } 
                // For student or if the structure is different
                else {
                    userObject = userData;
                    // Ensure role is set
                    userObject.role = role;
                }
            } else {
                // If userData is not available, create a minimal user object
                userObject = { role };
            }
            
            setUser(userObject);
            return { user: userObject };
        } catch (error) {
            console.error('Login error:', error);
            throw error;
        }
    };

    const register = async (userData, role) => {
        try {
            const endpoint = role === 'teacher' ? '/api/teachers/register' : '/api/students/register';
            const response = await api.post(endpoint, userData);
            return response.data;
        } catch (error) {
            console.error('Registration error:', error);
            throw error;
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        setUser(null);
    };

    const value = {
        user,
        loading,
        login,
        register,
        logout
    };

    return (
        <AuthContext.Provider value={value}>
            {children}
        </AuthContext.Provider>
    );
};

export default AuthContext;
