import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import CourseService from '../services/CourseService';
import CourseCreationTester from '../components/CourseCreationTester';

const TeacherDashboard = () => {
    const navigate = useNavigate();
    const [courses, setCourses] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [serverStatus, setServerStatus] = useState('checking');

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                setError('');
                
                // Fetch courses
                const coursesData = await CourseService.getTeacherCourses();
                setCourses(coursesData);
                
                // If we get here, the server is connected
                setServerStatus('connected');
                
            } catch (err) {
                console.error('Error fetching data:', err);
                setError('Failed to fetch data: ' + (err.message || 'Unknown error'));
                setServerStatus('disconnected');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    return (
        <div className="teacher-dashboard">
            <h1>Teacher Dashboard</h1>
            
            {error && <div className="error-message">{error}</div>}
            
            {serverStatus === 'disconnected' && (
                <div className="server-status-warning">
                    <div className="warning-message">
                        <p>We're having trouble connecting to the server. Please check your connection and try again.</p>
                    </div>
                </div>
            )}
            
            <main>
                {loading ? (
                    <p>Loading...</p>
                ) : (
                    <>
                        <h2>Your Courses</h2>
                        <div className="courses-grid">
                            {courses.length === 0 ? (
                                <p>No courses found. Create your first course!</p>
                            ) : (
                                courses.map(course => (
                                    <div key={course._id} className="course-card">
                                        <h3>{course.courseName}</h3>
                                        <p>{course.description}</p>
                                        <button onClick={() => navigate(`/courses/${course._id}`)}>
                                            View Details
                                        </button>
                                    </div>
                                ))
                            )}
                        </div>
                    </>
                )}
            </main>
            
            {process.env.NODE_ENV === 'development' && (
                <div className="debug-section">
                    <h2>Debug Tools (Development Only)</h2>
                    <CourseCreationTester />
                </div>
            )}
            
            <style jsx>{`
                .teacher-dashboard {
                    padding: 20px;
                }
                .error-message {
                    background-color: #f8d7da;
                    color: #721c24;
                    padding: 10px;
                    border-radius: 5px;
                    margin-bottom: 20px;
                }
                .server-status-warning {
                    margin-bottom: 20px;
                    padding: 15px;
                    border: 1px solid #f5c6cb;
                    border-radius: 5px;
                    background-color: #f8d7da;
                }
                .warning-message {
                    margin-top: 10px;
                    color: #721c24;
                }
                .courses-grid {
                    display: grid;
                    grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
                    gap: 20px;
                    margin-top: 20px;
                }
                .course-card {
                    border: 1px solid #ddd;
                    border-radius: 5px;
                    padding: 15px;
                    background-color: #f9f9f9;
                }
                .debug-section {
                    margin-top: 40px;
                    padding: 20px;
                    border: 1px dashed #999;
                    border-radius: 5px;
                    background-color: #f5f5f5;
                }
            `}</style>
        </div>
    );
};

export default TeacherDashboard;
