import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ExamService from '../services/ExamService';
import { useAuth } from '../context/AuthContext';

const StudentDashboard = () => {
    const { user, logout } = useAuth();
    const [activeExams, setActiveExams] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchActiveExams = async () => {
            try {
                setLoading(true);
                const examsData = await ExamService.getActiveExams();
                setActiveExams(examsData);
            } catch (err) {
                console.error('Error fetching exams:', err);
                setError('Failed to load exams. Please try again.');
            } finally {
                setLoading(false);
            }
        };

        fetchActiveExams();
    }, []);

    // Format date for display
    const formatDate = (dateString) => {
        const options = { year: 'numeric', month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
            {/* Modern Navbar with glass effect */}
            <nav className="bg-white bg-opacity-80 backdrop-filter backdrop-blur-lg border-b border-gray-200 shadow-nav sticky top-0 z-10">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between h-16">
                        <div className="flex items-center">
                            <div className="flex-shrink-0">
                                <h1 className="text-primary-600 text-2xl font-bold">Exam<span className="text-secondary-500">System</span></h1>
                            </div>
                        </div>
                        <div className="flex items-center space-x-4">
                            <div className="flex items-center space-x-2">
                                <div className="h-8 w-8 rounded-full bg-primary-100 flex items-center justify-center text-primary-700">
                                    {user?.firstName?.charAt(0) || 'U'}
                                </div>
                                <span className="text-gray-700 font-medium">Welcome, {user?.firstName}</span>
                            </div>
                            <button
                                onClick={logout}
                                className="bg-white border border-gray-300 text-gray-700 hover:bg-gray-50 px-4 py-2 rounded-md text-sm font-medium transition-all duration-200 shadow-sm hover:shadow"
                            >
                                Logout
                            </button>
                        </div>
                    </div>
                </div>
            </nav>
            
            {/* Dashboard Content */}
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
                <div className="mb-8">
                    <h2 className="text-3xl font-bold text-gray-800">Student Dashboard</h2>
                    <p className="text-gray-600 mt-1">View your exams and track your progress</p>
                </div>
                
                {/* Dashboard Grid */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {/* Stats Card */}
                    <div className="bg-white rounded-xl shadow-card p-6 hover:shadow-card-hover transition-all duration-300 border border-gray-100 transform hover:-translate-y-1">
                        <div className="flex items-center justify-between">
                            <h3 className="text-lg font-semibold text-gray-800">Upcoming Exams</h3>
                            <div className="p-2 bg-secondary-100 rounded-lg">
                                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-secondary-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
                                </svg>
                            </div>
                        </div>
                        <p className="text-3xl font-bold text-gray-800 mt-4">3</p>
                        <p className="text-gray-600 text-sm font-medium mt-2">Next exam in 2 days</p>
                    </div>
                    
                    {/* Active Exams Section */}
                    <div className="bg-white rounded-xl shadow-card p-6 hover:shadow-card-hover transition-all duration-300 border border-gray-100 transform hover:-translate-y-1">
                        <div className="mb-6">
                            <h2 className="text-2xl font-bold text-gray-900">Active Exams</h2>
                            <p className="mt-1 text-sm text-gray-500">
                                These are the exams that are currently available for you to take.
                            </p>
                        </div>

                        {loading ? (
                            <div className="text-center py-10">
                                <div className="spinner"></div>
                                <p className="mt-2 text-gray-600">Loading exams...</p>
                            </div>
                        ) : activeExams.length === 0 ? (
                            <div className="bg-white shadow overflow-hidden sm:rounded-lg">
                                <div className="px-4 py-5 sm:p-6 text-center">
                                    <p className="text-gray-500">There are no active exams at the moment.</p>
                                    <p className="text-gray-500 mt-2">
                                        Check back later for upcoming exams.
                                    </p>
                                </div>
                            </div>
                        ) : (
                            <div className="bg-white shadow overflow-hidden sm:rounded-lg">
                                {activeExams.map((exam) => (
                                    <div key={exam._id} className="bg-white shadow rounded-lg p-4 mb-4">
                                        <div className="flex justify-between items-center">
                                            <div>
                                                <h3 className="text-lg font-semibold text-gray-900">{exam.title}</h3>
                                                <p className="text-sm text-gray-500">
                                                    {new Date(exam.startTime).toLocaleString()} - {new Date(exam.endTime).toLocaleString()}
                                                </p>
                                                <p className="text-sm text-gray-600 mt-1">Duration: {exam.duration} minutes</p>
                                            </div>
                                            <div>
                                                <Link
                                                    to={`/take-exam/${exam._id}`}
                                                    className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-md text-sm font-medium"
                                                >
                                                    Take Exam
                                                </Link>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>

                    {/* Exam Results Section */}
                    <div className="bg-white rounded-xl shadow-card p-6 hover:shadow-card-hover transition-all duration-300 border border-gray-100 transform hover:-translate-y-1">
                        <div className="mb-6">
                            <h2 className="text-2xl font-bold text-gray-900">My Exam Results</h2>
                            <p className="mt-1 text-sm text-gray-500">
                                View your past exam results and performance.
                            </p>
                        </div>
                        
                        <div className="bg-white shadow overflow-hidden sm:rounded-lg">
                            <div className="px-4 py-5 sm:p-6 text-center">
                                <p className="text-gray-500">Results will be displayed here after you complete exams.</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default StudentDashboard;
