import axios from 'axios';

// Get the API URL from environment variables or use a default
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5001/api';

const api = axios.create({
    baseURL: API_URL,
    headers: {
        'Content-Type': 'application/json',
    },
    withCredentials: true,
    timeout: 15000 // 15 second timeout
});

// Add a request interceptor to include auth token
api.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem('token');
        if (token) {
            config.headers['Authorization'] = `Bearer ${token}`;
        }
        console.log('API Request:', config.method.toUpperCase(), config.url);
        return config;
    },
    (error) => {
        console.error('Request error:', error);
        return Promise.reject(error);
    }
);

// Add a response interceptor to handle errors
api.interceptors.response.use(
    (response) => {
        console.log('API Response:', response.status, response.config.url);
        return response;
    },
    (error) => {
        console.error('API Error:', error);
        
        // Handle timeout errors
        if (error.code === 'ECONNABORTED') {
            console.error('Request timeout');
            error.message = 'Request timeout. Server is not responding.';
            return Promise.reject(new Error('Request timeout. Please try again later.'));
        }
        
        // Handle network errors (server not running)
        if (!error.response) {
            console.error('Network error - no response');
            return Promise.reject(new Error('Cannot connect to server. Please check if the server is running.'));
        } 
        
        // Handle authentication errors
        if (error.response.status === 401) {
            // Handle unauthorized access
            localStorage.removeItem('token');
            // Only redirect if we're not already on the login page
            if (!window.location.pathname.includes('/login')) {
                window.location.href = '/login';
            }
        }
        
        return Promise.reject(error);
    }
);

export default api;
