import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import config from '../config';

const StudentDashboard = () => {
    const [activeTab, setActiveTab] = useState('courses');
    const [courses, setCourses] = useState([]);
    const [exams, setExams] = useState([]);
    const [results, setResults] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const { user, logout } = useAuth();

    useEffect(() => {
        if (activeTab === 'courses') {
            fetchCourses();
        } else if (activeTab === 'exams') {
            fetchExams();
        } else if (activeTab === 'results') {
            fetchResults();
        }
    }, [activeTab]);

    // Fix the reference to 'course' by ensuring it's properly defined
    // in the component where it's being used (line 471)
    const renderCourseItem = (course) => (
        <div key={course._id} className="course-item">
            <h3 className="mt-2 text-sm font-medium text-gray-900">
                {course.courseCode || 'Course Code'}
            </h3>
            {/* Rest of course rendering */}
        </div>
    );

    // Add the missing fetchCourses function
    const fetchCourses = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const response = await axios.get(`${config.API_BASE_URL}/api/courses/my-courses`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setCourses(response.data);
        } catch (err) {
            setError('Failed to fetch courses');
            console.error('Error fetching courses:', err);
        } finally {
            setLoading(false);
        }
    };

    // Add the missing fetchExams function
    const fetchExams = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const response = await axios.get(`${config.API_BASE_URL}/api/exams/student`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setExams(response.data);
        } catch (err) {
            setError('Failed to fetch exams');
            console.error('Error fetching exams:', err);
        } finally {
            setLoading(false);
        }
    };

    // Add the missing fetchResults function
    const fetchResults = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const response = await axios.get(`${config.API_BASE_URL}/api/exams/results`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setResults(response.data);
        } catch (err) {
            setError('Failed to fetch results');
            console.error('Error fetching results:', err);
        } finally {
            setLoading(false);
        }
    };

    // Add a render function for the dashboard
    return (
        <div className="student-dashboard">
            <h1>Student Dashboard</h1>
            <div className="tabs">
                <button onClick={() => setActiveTab('courses')}>Courses</button>
                <button onClick={() => setActiveTab('exams')}>Exams</button>
                <button onClick={() => setActiveTab('results')}>Results</button>
            </div>
            
            {error && <div className="error">{error}</div>}
            
            {activeTab === 'courses' && (
                <div className="courses-section">
                    <h2>My Courses</h2>
                    {loading ? (
                        <p>Loading courses...</p>
                    ) : (
                        <div className="courses-list">
                            {courses.length > 0 ? (
                                courses.map(course => renderCourseItem(course))
                            ) : (
                                <p>No courses found.</p>
                            )}
                        </div>
                    )}
                </div>
            )}
            
            {/* Similar sections for exams and results */}
        </div>
    );
};

// Add the missing default export
export default StudentDashboard;
