// Try to get a previously successful base URL from localStorage
const savedBaseUrl = localStorage.getItem('successful_api_url');

// Default API URLs to try
const DEFAULT_API_URLS = [
    'http://localhost:5001',
    'http://localhost:5000',
    'http://localhost:3001',
    window.location.origin + '/api'
];

// Get API URL from environment variables, localStorage, or use default
const API_BASE_URL = process.env.REACT_APP_API_URL || savedBaseUrl || DEFAULT_API_URLS[0];

console.log('Using API URL:', API_BASE_URL);

const config = {
    API_BASE_URL,
    DEFAULT_API_URLS
};

export default config;
