import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import ExamService from '../services/ExamService';
import { useAuth } from '../context/AuthContext';

const TeacherDashboard = () => {
    const { user, logout } = useAuth();
    const navigate = useNavigate();
    const [exams, setExams] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        // Check if user is logged in and is a teacher
        if (!user) {
            navigate('/login');
            return;
        }

        if (user.role !== 'teacher') {
            navigate('/student-dashboard');
            return;
        }

        const fetchExams = async () => {
            try {
                setLoading(true);
                const examsData = await ExamService.getTeacherExams();
                setExams(examsData);
                setError('');
            } catch (err) {
                console.error('Error fetching exams:', err);
                if (err.response && err.response.status === 401) {
                    // If unauthorized, redirect to login
                    logout();
                    navigate('/login');
                } else {
                    setError('Failed to load exams. Please try again.');
                }
            } finally {
                setLoading(false);
            }
        };

        fetchExams();
    }, [user, navigate, logout]);

    const handleDeleteExam = async (examId) => {
        if (window.confirm('Are you sure you want to delete this exam?')) {
            try {
                setLoading(true);
                console.log(`Attempting to delete exam ${examId}`);
                
                await ExamService.deleteExam(examId);
                
                // Update the UI after successful deletion
                setExams(prevExams => prevExams.filter(exam => exam._id !== examId));
                console.log('Exam deleted successfully');
                
            } catch (err) {
                console.error('Error deleting exam:', err);
                const errorMessage = err.message || 'Failed to delete exam. Please try again.';
                setError(errorMessage);
            } finally {
                setLoading(false);
            }
        }
    };

    const formatDate = (dateString) => {
        const options = { year: 'numeric', month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' };
        return new Date(dateString).toLocaleDateString(undefined, options);
    };

    const renderStatusBadge = (startTime, endTime) => {
        const now = new Date();
        const start = new Date(startTime);
        const end = new Date(endTime);

        if (now < start) {
            return (
                <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-yellow-100 text-yellow-800">
                    Upcoming
                </span>
            );
        } else if (now >= start && now <= end) {
            return (
                <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                    Active
                </span>
            );
        } else {
            return (
                <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-gray-100 text-gray-800">
                    Completed
                </span>
            );
        }
    };

    return (
        <div className="min-h-screen bg-dark-100 text-light-100">
            {/* Minimal sidebar */}
            <div className="fixed inset-y-0 left-0 w-16 bg-dark-200 flex flex-col items-center py-6 space-y-8">
                <div className="w-10 h-10 rounded-full bg-accent-100 flex items-center justify-center text-light-100 font-bold">
                    {user?.firstName?.charAt(0) || 'U'}
                </div>
                
                <div className="flex flex-col space-y-6">
                    <button className="w-10 h-10 rounded-lg bg-dark-300 flex items-center justify-center text-light-300 hover:bg-accent-100 hover:text-light-100 transition-all">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
                        </svg>
                    </button>
                    
                    <button className="w-10 h-10 rounded-lg bg-accent-100 flex items-center justify-center text-light-100">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                        </svg>
                    </button>
                    
                    <button className="w-10 h-10 rounded-lg bg-dark-300 flex items-center justify-center text-light-300 hover:bg-accent-100 hover:text-light-100 transition-all">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                        </svg>
                    </button>
                </div>
                
                <div className="mt-auto">
                    <button onClick={logout} className="w-10 h-10 rounded-lg bg-dark-300 flex items-center justify-center text-light-300 hover:bg-red-500 hover:text-light-100 transition-all">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 16l4-4m0 0l-4-4m4 4H7m6 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h4a3 3 0 013 3v1" />
                        </svg>
                    </button>
                </div>
            </div>
            
            {/* Main content */}
            <div className="ml-16 p-8">
                <div className="flex justify-between items-center mb-8">
                    <h1 className="text-2xl font-bold">Teacher Dashboard</h1>
                    <div className="flex items-center space-x-2">
                        <span className="text-light-300">Welcome, {user?.firstName}</span>
                    </div>
                </div>
                
                {/* Dashboard content */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {/* Stats card */}
                    <div className="bg-dark-200 rounded-lg p-6 border border-dark-300">
                        <div className="flex justify-between items-center mb-4">
                            <h3 className="text-light-200 font-medium">Total Exams</h3>
                            <div className="w-8 h-8 rounded-md bg-accent-100 bg-opacity-20 flex items-center justify-center">
                                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-accent-100" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                                </svg>
                            </div>
                        </div>
                        <p className="text-3xl font-bold text-light-100">24</p>
                        <div className="mt-2 flex items-center text-green-400 text-sm">
                            <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 10l7-7m0 0l7 7m-7-7v18" />
                            </svg>
                            <span>12% from last month</span>
                        </div>
                    </div>
                    
                    {error && (
                        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-6" role="alert">
                            <p>{error}</p>
                        </div>
                    )}

                    <div className="px-4 py-6 sm:px-0">
                        <div className="flex justify-between items-center mb-6">
                            <h2 className="text-2xl font-bold text-gray-900">My Exams</h2>
                            <div>
                                <Link
                                    to="/create-exam"
                                    className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded-md text-sm font-medium"
                                >
                                    Create an Exam
                                </Link>
                            </div>
                        </div>

                        {loading ? (
                            <div className="text-center py-10">
                                <div className="spinner"></div>
                                <p className="mt-2 text-gray-600">Loading exams...</p>
                            </div>
                        ) : exams.length === 0 ? (
                            <div className="bg-white shadow overflow-hidden sm:rounded-lg">
                                <div className="px-4 py-5 sm:p-6 text-center">
                                    <p className="text-gray-500">You haven't created any exams yet.</p>
                                    <p className="text-gray-500 mt-2">
                                        Click on "Create MCQ Exam" or "Create Written Exam" to get started.
                                    </p>
                                </div>
                            </div>
                        ) : (
                            <div className="bg-white shadow overflow-hidden sm:rounded-lg">
                                <table className="min-w-full divide-y divide-gray-200">
                                    <thead className="bg-gray-50">
                                        <tr>
                                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Title</th>
                                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Type</th>
                                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Duration</th>
                                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Start Time</th>
                                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">End Time</th>
                                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                                            <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                                        </tr>
                                    </thead>
                                    <tbody className="bg-white divide-y divide-gray-200">
                                        {exams.map((exam) => (
                                            <tr key={exam._id}>
                                                <td className="px-6 py-4 whitespace-nowrap">
                                                    <div className="text-sm font-medium text-gray-900">{exam.title}</div>
                                                </td>
                                                <td className="px-6 py-4 whitespace-nowrap">
                                                    <div className="text-sm text-gray-500">
                                                        {exam.examType === 'mcq' ? 'Multiple Choice' : 'Written'}
                                                    </div>
                                                </td>
                                                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                                    {exam.duration} minutes
                                                </td>
                                                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                                    {formatDate(exam.startTime)}
                                                </td>
                                                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                                    {formatDate(exam.endTime)}
                                                </td>
                                                <td className="px-6 py-4 whitespace-nowrap">
                                                    {renderStatusBadge(exam.startTime, exam.endTime)}
                                                </td>
                                                <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                                                    <Link to={`/exam/${exam._id}`} className="text-indigo-600 hover:text-indigo-900 mr-4">
                                                        View
                                                    </Link>
                                                    <Link to={`/exam/${exam._id}/results`} className="text-green-600 hover:text-green-900 mr-4">
                                                        Results
                                                    </Link>
                                                    <Link to={`/edit-exam/${exam._id}`} className="text-blue-600 hover:text-blue-900 mr-4">
                                                        Edit
                                                    </Link>
                                                    <button
                                                        onClick={() => handleDeleteExam(exam._id)}
                                                        className="text-red-600 hover:text-red-900"
                                                    >
                                                        Delete
                                                    </button>
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default TeacherDashboard;
