import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import ExamService from '../services/ExamService';

const ExamDetails = () => {
    const { examId } = useParams();
    const navigate = useNavigate();
    const { user } = useAuth();
    const [exam, setExam] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const handleDeleteExam = async () => {
        if (window.confirm('Are you sure you want to delete this exam? This action cannot be undone.')) {
            try {
                setLoading(true);
                await ExamService.deleteExam(examId);
                navigate('/teacher-dashboard', { state: { message: 'Exam deleted successfully' } });
            } catch (err) {
                console.error('Error deleting exam:', err);
                const errorMessage = err.message || 'Failed to delete exam. Please try again.';
                setError(errorMessage);
                setLoading(false);
            }
        }
    };

    useEffect(() => {
        // Check if user is logged in
        if (!user) {
            navigate('/login');
            return;
        }

        // Fetch exam details
        const fetchExam = async () => {
            try {
                setLoading(true);
                const examData = await ExamService.getExamById(examId);
                setExam(examData);
                setLoading(false);
            } catch (err) {
                console.error('Error fetching exam:', err);
                setError('Failed to load exam details. Please try again.');
                setLoading(false);
            }
        };

        fetchExam();
    }, [examId, navigate, user]);

    if (loading) {
        return (
            <div className="min-h-screen bg-gray-50 flex items-center justify-center">
                <div className="spinner-border text-primary" role="status">
                    <span className="sr-only">Loading...</span>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="min-h-screen bg-gray-50 flex items-center justify-center">
                <div className="bg-white p-8 rounded-lg shadow-md max-w-md w-full">
                    <h2 className="text-2xl font-bold text-red-600 mb-4">Error</h2>
                    <p className="text-gray-700">{error}</p>
                    <button
                        onClick={() => navigate(-1)}
                        className="mt-6 bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    >
                        Go Back
                    </button>
                </div>
            </div>
        );
    }

    if (!exam) {
        return (
            <div className="min-h-screen bg-gray-50 flex items-center justify-center">
                <div className="bg-white p-8 rounded-lg shadow-md max-w-md w-full">
                    <h2 className="text-2xl font-bold text-gray-800 mb-4">Exam Not Found</h2>
                    <p className="text-gray-700">The exam you're looking for doesn't exist or you don't have permission to access it.</p>
                    <button
                        onClick={() => navigate(-1)}
                        className="mt-6 bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    >
                        Go Back
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-50 py-8 px-4 sm:px-6 lg:px-8">
            <div className="max-w-4xl mx-auto bg-white p-6 rounded-lg shadow-md">
                <div className="mb-6 border-b pb-4">
                    <h1 className="text-2xl font-bold text-gray-900">{exam.title}</h1>
                    <p className="text-gray-600 mt-2">{exam.description}</p>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                    <div>
                        <h2 className="text-lg font-semibold mb-2">Exam Details</h2>
                        <p><span className="font-medium">Type:</span> {exam.examType === 'mcq' ? 'Multiple Choice' : 'Written'}</p>
                        <p><span className="font-medium">Duration:</span> {exam.duration} minutes</p>
                        <p><span className="font-medium">Start Time:</span> {new Date(exam.startTime).toLocaleString()}</p>
                        <p><span className="font-medium">End Time:</span> {new Date(exam.endTime).toLocaleString()}</p>
                    </div>
                </div>

                <div className="mt-6">
                    <h2 className="text-lg font-semibold mb-4">Questions</h2>
                    {exam.questions && exam.questions.length > 0 ? (
                        <div className="space-y-6">
                            {exam.questions.map((question, index) => (
                                <div key={index} className="bg-gray-50 p-4 rounded-md">
                                    <p className="font-medium">Question {index + 1}: {question.questionText || question.text}</p>
                                    {(question.questionType === 'multiple-choice' || question.type === 'multiple-choice') && (
                                        <div className="mt-2 ml-4">
                                            {question.options && question.options.map((option, optIndex) => (
                                                <div key={optIndex} className="flex items-start mt-1">
                                                    <span className="mr-2">{String.fromCharCode(65 + optIndex)}.</span>
                                                    <span>{typeof option === 'object' ? option.text : option}</span>
                                                </div>
                                            ))}
                                        </div>
                                    )}
                                </div>
                            ))}
                        </div>
                    ) : (
                        <p className="text-gray-500">No questions have been added to this exam yet.</p>
                    )}
                </div>

                <div className="mt-8 flex justify-between">
                    <button
                        onClick={() => navigate(-1)}
                        className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded-md"
                    >
                        Back
                    </button>
                    <div>
                        <button
                            onClick={() => navigate(`/edit-exam/${examId}`)}
                            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md mr-2"
                        >
                            Edit Exam
                        </button>
                        <button
                            onClick={() => navigate(`/add-questions/${examId}`)}
                            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-md mr-2"
                        >
                            Add Questions
                        </button>
                        <button
                            onClick={handleDeleteExam}
                            className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-md"
                        >
                            Delete Exam
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ExamDetails;
