import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const SignUp = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        password: '',
        role: 'student', // Default role
        // Student specific fields
        studentId: '',
        department: '',
        level: '',
        // Teacher specific fields
        subjects: '',
        phoneNumber: ''
    });
    const [error, setError] = useState('');
    const { register } = useAuth();
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        
        try {
            // Validate form data
            if (!formData.firstName || !formData.lastName || !formData.email || !formData.password) {
                setError('Please fill in all required fields');
                return;
            }
            
            if (formData.password.length < 6) {
                setError('Password must be at least 6 characters long');
                return;
            }
            
            // Create a copy of formData with only the relevant fields based on role
            const dataToSubmit = {
                firstName: formData.firstName,
                lastName: formData.lastName,
                email: formData.email,
                password: formData.password,
                role: formData.role
            };
            
            // Add role-specific fields
            if (formData.role === 'teacher') {
                if (!formData.department) {
                    setError('Department is required for teachers');
                    return;
                }
                dataToSubmit.department = formData.department;
                dataToSubmit.phoneNumber = formData.phoneNumber;
                dataToSubmit.subjects = formData.subjects.length ? 
                    formData.subjects : 
                    formData.subjects === '' ? 
                        ['General'] : 
                        formData.subjects.split(',').map(s => s.trim());
            } else if (formData.role === 'student') {
                if (!formData.department) {
                    setError('Department is required for students');
                    return;
                }
                dataToSubmit.studentId = formData.studentId || `STU${Date.now().toString().slice(-6)}`;
                dataToSubmit.department = formData.department;
                dataToSubmit.level = parseInt(formData.level) || 1;
                dataToSubmit.phoneNumber = formData.phoneNumber;
            }
            
            console.log('Submitting registration data:', dataToSubmit);
            const result = await register(dataToSubmit);
            console.log('Registration result:', result);
            
            // Redirect based on role
            if (formData.role === 'student') {
                navigate('/student-dashboard');
            } else {
                navigate('/teacher-dashboard');
            }
        } catch (err) {
            console.error('Registration error:', err);
            if (err.response) {
                console.error('Server response:', err.response.data);
                setError(err.response.data.message || 'Registration failed');
            } else if (err.request) {
                console.error('No response received:', err.request);
                setError('No response from server. Please check your connection.');
            } else {
                console.error('Error setting up request:', err.message);
                setError('Error setting up request: ' + err.message);
            }
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
            <div className="max-w-md w-full space-y-8">
                <div>
                    <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
                        Create your account
                    </h2>
                </div>
                <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
                    {error && (
                        <div className="text-red-500 text-center">{error}</div>
                    )}
                    <div className="rounded-md shadow-sm -space-y-px">
                        <div>
                            <label htmlFor="role" className="sr-only">
                                Role
                            </label>
                            <select
                                id="role"
                                name="role"
                                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                                value={formData.role}
                                onChange={handleChange}
                            >
                                <option value="student">Student</option>
                                <option value="teacher">Teacher</option>
                            </select>
                        </div>
                        <div>
                            <label htmlFor="firstName" className="sr-only">
                                First Name
                            </label>
                            <input
                                id="firstName"
                                name="firstName"
                                type="text"
                                required
                                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                                placeholder="First Name"
                                value={formData.firstName}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <label htmlFor="lastName" className="sr-only">
                                Last Name
                            </label>
                            <input
                                id="lastName"
                                name="lastName"
                                type="text"
                                required
                                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                                placeholder="Last Name"
                                value={formData.lastName}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <label htmlFor="email" className="sr-only">
                                Email address
                            </label>
                            <input
                                id="email"
                                name="email"
                                type="email"
                                required
                                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                                placeholder="Email address"
                                value={formData.email}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <label htmlFor="password" className="sr-only">
                                Password
                            </label>
                            <input
                                id="password"
                                name="password"
                                type="password"
                                required
                                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                                placeholder="Password"
                                value={formData.password}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <label htmlFor="department" className="sr-only">
                                Department
                            </label>
                            <input
                                id="department"
                                name="department"
                                type="text"
                                required
                                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                                placeholder="Department"
                                value={formData.department}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <label htmlFor="phoneNumber" className="sr-only">
                                Phone Number
                            </label>
                            <input
                                id="phoneNumber"
                                name="phoneNumber"
                                type="tel"
                                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                                placeholder="Phone Number (optional)"
                                value={formData.phoneNumber}
                                onChange={handleChange}
                            />
                        </div>
                        {formData.role === 'student' && (
                            <>
                                <div>
                                    <label htmlFor="studentId" className="sr-only">
                                        Student ID
                                    </label>
                                    <input
                                        id="studentId"
                                        name="studentId"
                                        type="text"
                                        required
                                        className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                                        placeholder="Student ID"
                                        value={formData.studentId}
                                        onChange={handleChange}
                                    />
                                </div>
                                <div>
                                    <label htmlFor="level" className="sr-only">
                                        Level
                                    </label>
                                    <input
                                        id="level"
                                        name="level"
                                        type="number"
                                        min="1"
                                        max="5"
                                        required
                                        className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                                        placeholder="Level (1-5)"
                                        value={formData.level}
                                        onChange={handleChange}
                                    />
                                </div>
                            </>
                        )}
                        {formData.role === 'teacher' && (
                            <div>
                                <label htmlFor="subjects" className="sr-only">
                                    Subjects
                                </label>
                                <input
                                    id="subjects"
                                    name="subjects"
                                    type="text"
                                    required
                                    className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-b-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                                    placeholder="Subjects (comma-separated)"
                                    value={formData.subjects}
                                    onChange={handleChange}
                                />
                            </div>
                        )}
                    </div>

                    <div>
                        <button
                            type="submit"
                            className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                        >
                            Sign up
                        </button>
                    </div>
                </form>
                <div className="text-center">
                    <p className="text-sm text-gray-600">
                        Already have an account?{' '}
                        <a href="/login" className="font-medium text-indigo-600 hover:text-indigo-500">
                            Sign in
                        </a>
                    </p>
                </div>
            </div>
        </div>
    );
};

export default SignUp;
