import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import config from '../config';
import '../styles/Dashboard.css';

const StudentDashboard = () => {
    const { user, logout } = useAuth();
    const [courses, setCourses] = useState([]);
    const [activeExams, setActiveExams] = useState([]);
    const [pastExams, setPastExams] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [activeTab, setActiveTab] = useState('courses');
    const [selectedExam, setSelectedExam] = useState(null);
    const [examInProgress, setExamInProgress] = useState(false);
    const [currentAnswers, setCurrentAnswers] = useState({});
    const [timeLeft, setTimeLeft] = useState(0);
    const [timerInterval, setTimerInterval] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                const token = localStorage.getItem('token');
                
                // Fetch courses
                const coursesResponse = await axios.get(`${config.API_BASE_URL}/api/courses`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setCourses(coursesResponse.data);
                
                // Fetch active exams
                const activeExamsResponse = await axios.get(`${config.API_BASE_URL}/api/exams/active`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setActiveExams(activeExamsResponse.data);
                
                // Fetch past exams (submissions)
                const pastExamsResponse = await axios.get(`${config.API_BASE_URL}/api/submissions/my-submissions`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setPastExams(pastExamsResponse.data);
                
            } catch (err) {
                setError('Failed to fetch data');
                console.error('Error fetching data:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
        
        // Clean up timer on unmount
        return () => {
            if (timerInterval) {
                clearInterval(timerInterval);
            }
        };
    }, []);
    
    const startExam = async (examId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`${config.API_BASE_URL}/api/exams/${examId}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            
            setSelectedExam(response.data);
            setExamInProgress(true);
            
            // Initialize answers object
            const initialAnswers = {};
            response.data.questions.forEach((q, index) => {
                initialAnswers[index] = '';
            });
            setCurrentAnswers(initialAnswers);
            
            // Set timer
            const durationInSeconds = response.data.duration * 60;
            setTimeLeft(durationInSeconds);
            
            // Start countdown
            const interval = setInterval(() => {
                setTimeLeft(prev => {
                    if (prev <= 1) {
                        clearInterval(interval);
                        submitExam();
                        return 0;
                    }
                    return prev - 1;
                });
            }, 1000);
            
            setTimerInterval(interval);
            
        } catch (err) {
            setError('Failed to start exam');
            console.error('Error starting exam:', err);
        }
    };
    
    const handleAnswerChange = (questionIndex, value) => {
        setCurrentAnswers(prev => ({
            ...prev,
            [questionIndex]: value
        }));
    };
    
    const submitExam = async () => {
        try {
            // Clear timer
            if (timerInterval) {
                clearInterval(timerInterval);
                setTimerInterval(null);
            }
            
            const token = localStorage.getItem('token');
            
            // Format answers for submission
            const formattedAnswers = Object.keys(currentAnswers).map(index => ({
                questionId: selectedExam.questions[index]._id,
                selectedAnswer: currentAnswers[index]
            }));
            
            await axios.post(
                `${config.API_BASE_URL}/api/exams/${selectedExam._id}/submit`,
                { answers: formattedAnswers },
                { headers: { Authorization: `Bearer ${token}` }}
            );
            
            // Reset exam state
            setExamInProgress(false);
            setSelectedExam(null);
            
            // Refresh past exams
            const pastExamsResponse = await axios.get(`${config.API_BASE_URL}/api/submissions/my-submissions`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setPastExams(pastExamsResponse.data);
            
            alert('Exam submitted successfully! Your results will be available after grading.');
        } catch (err) {
            setError('Failed to submit exam');
            console.error('Error submitting exam:', err);
        }
    };
    
    const formatTime = (seconds) => {
        const minutes = Math.floor(seconds / 60);
        const remainingSeconds = seconds % 60;
        return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
    };
    
    const handleLogout = async () => {
        try {
            await logout();
            navigate('/login');
        } catch (error) {
            console.error('Logout failed:', error);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <div className="text-xl">Loading...</div>
            </div>
        );
    }

    if (examInProgress && selectedExam) {
        return (
            <div className="min-h-screen bg-gray-100 p-4">
                <div className="timer bg-white shadow-md p-2 rounded-md fixed top-4 right-4">
                    Time Remaining: <span className="font-bold">{formatTime(timeLeft)}</span>
                </div>
                
                <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-md p-6 mt-12">
                    <h1 className="text-2xl font-bold mb-6">{selectedExam.title}</h1>
                    
                    <div className="mb-8">
                        <p className="text-gray-600">{selectedExam.description}</p>
                        <p className="text-sm text-gray-500 mt-2">
                            Total Questions: {selectedExam.questions.length} | 
                            Type: {selectedExam.examType === 'mcq' ? 'Multiple Choice' : 'Written'}
                        </p>
                    </div>
                    
                    <div className="space-y-8">
                        {selectedExam.questions.map((question, index) => (
                            <div key={index} className="p-4 border rounded-md">
                                <div className="flex items-start mb-4">
                                    <span className="bg-blue-100 text-blue-800 font-medium rounded-full w-6 h-6 flex items-center justify-center mr-3">
                                        {index + 1}
                                    </span>
                                    <h3 className="text-lg font-medium">{question.questionText}</h3>
                                </div>
                                
                                {selectedExam.examType === 'mcq' ? (
                                    <div className="ml-9 space-y-2">
                                        {question.options.map((option, optIndex) => (
                                            <div 
                                                key={optIndex}
                                                className={`p-3 border rounded-md cursor-pointer ${
                                                    currentAnswers[index] === option ? 'bg-blue-50 border-blue-300' : ''
                                                }`}
                                                onClick={() => handleAnswerChange(index, option)}
                                            >
                                                <div className="flex items-center">
                                                    <input
                                                        type="radio"
                                                        name={`question-${index}`}
                                                        checked={currentAnswers[index] === option}
                                                        onChange={() => handleAnswerChange(index, option)}
                                                        className="mr-3"
                                                    />
                                                    <span>{option}</span>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                ) : (
                                    <div className="ml-9">
                                        <textarea
                                            value={currentAnswers[index]}
                                            onChange={(e) => handleAnswerChange(index, e.target.value)}
                                            className="w-full p-3 border rounded-md min-h-[150px]"
                                            placeholder="Type your answer here..."
                                        />
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>
                    
                    <div className="mt-8 flex justify-end">
                        <button
                            onClick={submitExam}
                            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-md font-medium"
                        >
                            Submit Exam
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-100">
            <nav className="bg-white shadow-sm">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="flex justify-between h-16">
                        <div className="flex items-center">
                            <h1 className="text-xl font-semibold">Student Dashboard</h1>
                        </div>
                        <div className="flex items-center">
                            <span className="mr-4">Welcome, {user?.firstName} {user?.lastName}</span>
                            <button
                                onClick={handleLogout}
                                className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-md"
                            >
                                Logout
                            </button>
                        </div>
                    </div>
                </div>
            </nav>

            <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
                {error && (
                    <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
                        {error}
                    </div>
                )}

                <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
                    {courses.map(course => (
                        <div key={course._id} className="bg-white overflow-hidden shadow rounded-lg">
                            <div className="px-4 py-5 sm:p-6">
                                <h3 className="text-lg leading-6 font-medium text-gray-900">
                                    {course.name}
                                </h3>
                                <p className="mt-1 max-w-2xl text-sm text-gray-500">
                                    {course.description}
                                </p>
                                <div className="mt-4">
                                    <span className="inline-flex items-center px-3 py-0.5 rounded-full text-sm font-medium bg-green-100 text-green-800">
                                        {course.teacher.firstName} {course.teacher.lastName}
                                    </span>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </main>
        </div>
    );
};

export default StudentDashboard;
