import api from './api';
import config from '../config';

const API_PATH = '/api/exams';

class ExamService {
    // Get all exams for a teacher
    async getTeacherExams() {
        try {
            const response = await api.get(`${API_PATH}/teacher`);
            return response.data;
        } catch (error) {
            console.error('Error fetching teacher exams:', error);
            throw error;
        }
    }

    // Get active exams for students
    async getActiveExams() {
        try {
            const response = await api.get(`${API_PATH}/active`);
            return response.data;
        } catch (error) {
            console.error('Error fetching active exams:', error);
            throw error;
        }
    }

    // Get exam details by ID
    async getExamById(examId) {
        try {
            const response = await api.get(`${API_PATH}/${examId}`);
            return response.data;
        } catch (error) {
            console.error('Error fetching exam details:', error);
            throw error;
        }
    }

    // Create a new exam
    async createExam(examData) {
        try {
            const response = await api.post(API_PATH, examData);
            return response.data;
        } catch (error) {
            console.error('Error creating exam:', error);
            throw error;
        }
    }

    // Update an exam
    async updateExam(examId, examData) {
        try {
            const response = await api.put(`${API_PATH}/${examId}`, examData);
            return response.data;
        } catch (error) {
            console.error('Error updating exam:', error);
            throw error;
        }
    }

    // Delete an exam
    async deleteExam(examId) {
        try {
            const response = await api.delete(`${API_PATH}/${examId}`);
            return response.data;
        } catch (error) {
            console.error('Error deleting exam:', error);
            throw error;
        }
    }

    // Submit an exam (for students)
    async submitExam(examId, answers) {
        try {
            const response = await api.post(`${API_PATH}/${examId}/submit`, { answers });
            return response.data;
        } catch (error) {
            console.error('Error submitting exam:', error);
            throw error;
        }
    }

    // Get exam results
    async getExamResults(examId) {
        try {
            const response = await api.get(`${API_PATH}/${examId}/results`);
            return response.data;
        } catch (error) {
            console.error('Error fetching exam results:', error);
            throw error;
        }
    }
}

export default new ExamService();
