import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ServerStatusChecker = () => {
    const [statuses, setStatuses] = useState([]);
    const [checking, setChecking] = useState(false);
    const [expandedDetails, setExpandedDetails] = useState(false);

    const checkAllServers = async () => {
        setChecking(true);
        setStatuses([]);

        const possibleBaseUrls = [
            'http://localhost:5001',
            'http://localhost:5000',
            'http://localhost:3001',
            window.location.origin + '/api'
        ];

        const endpoints = [
            '/health',
            '/api/health',
            '/courses',
            '/api/courses'
        ];

        const results = [];

        for (const baseUrl of possibleBaseUrls) {
            for (const endpoint of endpoints) {
                const url = `${baseUrl}${endpoint}`;
                try {
                    const startTime = Date.now();
                    const response = await axios.get(url, { timeout: 3000 });
                    const endTime = Date.now();

                    results.push({
                        url,
                        status: 'Connected',
                        statusCode: response.status,
                        responseTime: endTime - startTime,
                        data: response.data,
                        success: true
                    });

                    // If we found a working endpoint, save it
                    localStorage.setItem('successful_api_url', baseUrl);
                } catch (error) {
                    results.push({
                        url,
                        status: 'Failed',
                        error: error.message,
                        statusCode: error.response?.status || 'N/A',
                        success: false
                    });
                }
            }
        }

        setStatuses(results);
        setChecking(false);
    };

    useEffect(() => {
        checkAllServers();
    }, []);

    const workingEndpoints = statuses.filter(s => s.success);
    const hasWorkingEndpoint = workingEndpoints.length > 0;

    return (
        <div className="server-status-checker">
            <h3>Server Status</h3>

            <div className="status-summary">
                <div className={`status-indicator ${hasWorkingEndpoint ? 'connected' : 'disconnected'}`}>
                    {hasWorkingEndpoint ? 'Connected' : 'Disconnected'}
                </div>

                <button onClick={checkAllServers} disabled={checking}>
                    {checking ? 'Checking...' : 'Check Again'}
                </button>

                <button 
                    onClick={() => setExpandedDetails(!expandedDetails)}
                    className="details-toggle"
                >
                    {expandedDetails ? 'Hide Details' : 'Show Details'}
                </button>
            </div>

            {hasWorkingEndpoint && (
                <div className="working-endpoints">
                    <p>Working endpoints found:</p>
                    <ul>
                        {workingEndpoints.slice(0, 3).map((endpoint, index) => (
                            <li key={index}>
                                {endpoint.url} ({endpoint.responseTime}ms)
                            </li>
                        ))}
                        {workingEndpoints.length > 3 && <li>...and {workingEndpoints.length - 3} more</li>}
                    </ul>
                </div>
            )}

            {expandedDetails && (
                <div className="status-details">
                    <h4>Detailed Results:</h4>

                    {statuses.length === 0 && checking && (
                        <p>Checking server status...</p>
                    )}

                    {statuses.map((status, index) => (
                        <div 
                            key={index} 
                            className={`status-item ${status.success ? 'success' : 'error'}`}
                        >
                            <div className="status-url">{status.url}</div>
                            <div className="status-result">
                                <span className={status.success ? 'success' : 'error'}>
                                    {status.status} {status.statusCode}
                                </span>
                                {status.responseTime && <span className="response-time">{status.responseTime}ms</span>}
                            </div>
                            {status.error && <div className="error-message">{status.error}</div>}
                        </div>
                    ))}
                </div>
            )}

            <style jsx>{`
                .server-status-checker {
                    margin: 20px 0;
                    padding: 15px;
                    border: 1px solid #ddd;
                    border-radius: 5px;
                    background-color: #f9f9f9;
                }
                .status-summary {
                    display: flex;
                    align-items: center;
                    margin-bottom: 15px;
                }
                .status-indicator {
                    padding: 5px 10px;
                    border-radius: 4px;
                    font-weight: bold;
                    margin-right: 15px;
                }
                .connected {
                    background-color: #d4edda;
                    color: #155724;
                }
                .disconnected {
                    background-color: #f8d7da;
                    color: #721c24;
                }
                button {
                    background-color: #4CAF50;
                    color: white;
                    padding: 8px 12px;
                    border: none;
                    border-radius: 4px;
                    cursor: pointer;
                    margin-right: 10px;
                }
                button:disabled {
                    background-color: #cccccc;
                }
                .details-toggle {
                    background-color: #6c757d;
                }
                .working-endpoints {
                    margin-bottom: 15px;
                }
                .working-endpoints ul {
                    margin: 5px 0;
                    padding-left: 20px;
                }
                .status-details {
                    margin-top: 15px;
                }
                .status-item {
                    padding: 8px;
                    margin-bottom: 5px;
                    border-radius: 4px;
                    display: flex;
                    flex-direction: column;
                }
                .status-url {
                    font-family: monospace;
                    margin-bottom: 5px;
                }
                .status-result {
                    display: flex;
                    justify-content: space-between;
                }
                .success {
                    background-color: #e7f3e8;
                    border: 1px solid #c3e6cb;
                }
                .error {
                    background-color: #f8d7da;
                    border: 1px solid #f5c6cb;
                }
                .success .success {
                    color: #155724;
                }
                .error .error {
                    color: #721c24;
                }
                .response-time {
                    color: #6c757d;
                    font-size: 0.9em;
                }
                .error-message {
                    color: #721c24;
                    font-size: 0.9em;
                    margin-top: 5px;
                }
            `}</style>
        </div>
    );
};

export default ServerStatusChecker;
